mapper.select:
SELECT auup_authId as auupAuthId, auup_time as auupTime, auup_grade as auupGrade, auup_attr1 as auupAttr1, auup_attr2 as auupAttr2, auup_attr3 as auupAttr3, auup_attr4 as auupAttr4, auup_attr5 as auupAttr5 from(select * FROM authorupdate
auup_authId=#{auupAuthId}
ORDER BY auup_id DESC LIMIT 20) t ORDER BY auup_id asc;

SELECT
count(auth_id)
FROM
author


		auth_gender = #{gender}


		AND auth_wesiId = #{siteId}

SELECT
COUNT(id)
FROM
(
SELECT
auth_id AS id,
auth_works_num AS wor,
SUM(wo.work_totalHits) AS hit,
SUM(wo.work_commentsNum) AS com,
SUM(wo.work_totalRecoms) AS rec
FROM
author au
LEFT OUTER JOIN works wo
ON au.auth_id = wo.work_authId
GROUP BY
au.auth_id
) AS info



		hit >= #{minNum}


		AND hit < #{maxNum}




		com >= #{minNum}


		AND com < #{maxNum}




		rec >= #{minNum}


		AND rec < #{maxNum}




		wor >= #{minNum}


		AND wor < #{maxNum}

SELECT count(LEFT(auth_area, 2)) AS `value`, LEFT (auth_area, 2) AS `name` FROM `author` WHERE auth_area!=''
and auth_wesiId=#{siteId}
GROUP BY LEFT (auth_area, 2);

SELECT `t`.value,`name` FROM ( SELECT count(LEFT(auth_area, 2)) AS `value`, LEFT (auth_area, 2) as `name` FROM `author` WHERE auth_area!=''
and auth_wesiId=#{siteId}
GROUP BY LEFT (auth_area, 2))t ORDER BY t.value DESC LIMIT 0,1;

SELECT sum(`value`) from( SELECT count(LEFT(auth_area, 2)) AS `value` FROM `author` WHERE auth_area!=''
and auth_wesiId=#{siteId}
GROUP BY LEFT (auth_area, 2))t;
